// src/utils/storage.js

const GROUPS_KEY = 'pocket_notes_groups';

export function getGroups() {
  const data = localStorage.getItem(GROUPS_KEY);
  return data ? JSON.parse(data) : [];
}

export function saveGroups(groups) {
  localStorage.setItem(GROUPS_KEY, JSON.stringify(groups));
}

export function getNotes(groupName) {
  const data = localStorage.getItem(`notes_${groupName}`);
  return data ? JSON.parse(data) : [];
}

export function saveNotes(groupName, notes) {
  localStorage.setItem(`notes_${groupName}`, JSON.stringify(notes));
}
