import React, { useState, useEffect } from 'react';
import './App.css';
import { getGroups, saveGroups, getNotes, saveNotes } from './utils/storage';

function App() {
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [newGroupName, setNewGroupName] = useState('');
  const [newGroupColor, setNewGroupColor] = useState('#4a4eff');

  useEffect(() => {
    setGroups(getGroups());
  }, []);

  useEffect(() => {
    if (selectedGroup) {
      const savedNotes = getNotes(selectedGroup.name);
      setNotes(savedNotes);
    }
  }, [selectedGroup]);

  const handleAddGroup = () => {
    if (newGroupName.trim().length < 2) {
      alert('Group name must be at least 2 characters.');
      return;
    }
    if (groups.some(g => g.name === newGroupName.trim())) {
      alert('Group already exists!');
      return;
    }

    const newGroup = { name: newGroupName.trim(), color: newGroupColor };
    const updatedGroups = [...groups, newGroup];
    setGroups(updatedGroups);
    saveGroups(updatedGroups);
    setNewGroupName('');
    setNewGroupColor('#4a4eff');
    setShowPopup(false);
  };

  const handleNoteSave = () => {
    if (!newNote.trim()) return;
    const now = new Date().toLocaleString();
    const newNoteObj = {
      id: Date.now(),
      content: newNote,
      createdAt: now,
      updatedAt: now
    };
    const updatedNotes = [...notes, newNoteObj];
    setNotes(updatedNotes);
    saveNotes(selectedGroup.name, updatedNotes);
    setNewNote('');
  };

  return (
    <div className="app">
      <div className="sidebar">
        <h2>Pocket Notes</h2>
        <ul>
          {groups.map((group, idx) => (
            <li
              key={idx}
              onClick={() => setSelectedGroup(group)}
              className={selectedGroup?.name === group.name ? 'active' : ''}
            >
              <div className="avatar" style={{ backgroundColor: group.color }}>
                {group.name.slice(0, 2).toUpperCase()}
              </div>
              {group.name}
            </li>
          ))}
        </ul>
        <button onClick={() => setShowPopup(true)}>+ Create Group</button>
      </div>

      <div className="main">
        {selectedGroup ? (
          <>
            <h2>{selectedGroup.name}</h2>
            <div className="notes-display">
              {notes.map(note => (
                <div className="note" key={note.id}>
                  <p>{note.content}</p>
                  <small>Created: {note.createdAt}</small>
                  <small>Updated: {note.updatedAt}</small>
                </div>
              ))}
            </div>
            <div className="note-input">
              <input
                type="text"
                placeholder="Write a note..."
                value={newNote}
                onChange={e => setNewNote(e.target.value)}
                onKeyDown={e => e.key === 'Enter' && handleNoteSave()}
              />
              <button
                onClick={handleNoteSave}
                className={newNote.trim() ? 'active' : ''}
              >
                âž¤
              </button>
            </div>
          </>
        ) : (
          <div className="placeholder">
            <img src="/welcome.png" alt="Welcome" />
            <h1>Pocket Notes</h1>
            <p>Send and receive messages without keeping your phone online.</p>
            <p>Use Pocket Notes on up to 4 linked devices and 1 mobile phone</p>
            <p style={{ fontSize: '12px', marginTop: '10px' }}>ðŸ”’ end-to-end encrypted</p>
          </div>
        )}
      </div>

      {showPopup && (
        <div className="popup-overlay" onClick={() => setShowPopup(false)}>
          <div className="popup" onClick={e => e.stopPropagation()}>
            <h3>Create New Group</h3>
            <input
              type="text"
              placeholder="Group Name"
              value={newGroupName}
              onChange={e => setNewGroupName(e.target.value)}
            />
            <label>
              Choose color: &nbsp;
              <input
                type="color"
                value={newGroupColor}
                onChange={e => setNewGroupColor(e.target.value)}
              />
            </label>
            <button onClick={handleAddGroup}>Create</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
