import React, { useState, useEffect } from 'react';
import './App.css';
import { getGroups, saveGroups, getNotes, saveNotes } from './utils/storage';

function App() {
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [newGroupName, setNewGroupName] = useState('');
  const [newGroupColor, setNewGroupColor] = useState('#4a4eff');

  useEffect(() => {
    const savedGroups = getGroups();
    setGroups(savedGroups);
  }, []);

  useEffect(() => {
    if (selectedGroup) {
      const savedNotes = getNotes(selectedGroup);
      setNotes(savedNotes);
    }
  }, [selectedGroup]);

  const handleAddGroup = () => {
    if (
      newGroupName.length < 2 ||
      groups.find((g) => g.name.toLowerCase() === newGroupName.toLowerCase())
    )
      return;

    const newGroup = {
      name: newGroupName,
      color: newGroupColor,
    };

    const updatedGroups = [...groups, newGroup];
    setGroups(updatedGroups);
    saveGroups(updatedGroups);
    setNewGroupName('');
    setNewGroupColor('#4a4eff');
    setShowPopup(false);
  };

  const handleNoteSave = () => {
    if (!newNote.trim()) return;

    const now = new Date();
    const newNoteObj = {
      id: Date.now(),
      content: newNote,
      createdAt: now.toLocaleString(),
      updatedAt: now.toLocaleString(),
    };

    const updatedNotes = [...notes, newNoteObj];
    setNotes(updatedNotes);
    saveNotes(selectedGroup, updatedNotes);
    setNewNote('');
  };

  return (
    <div className="app">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Groups</h2>
        <ul>
          {groups.map((group, i) => (
            <li
              key={i}
              className={group.name === selectedGroup ? 'active' : ''}
              onClick={() => setSelectedGroup(group.name)}
            >
              <span className="avatar" style={{ backgroundColor: group.color }}>
                {group.name.slice(0, 2).toUpperCase()}
              </span>
              {group.name}
            </li>
          ))}
        </ul>
        <button onClick={() => setShowPopup(true)}>+ Create Group</button>
      </div>

      {/* Main Panel */}
      <div className="main">
        {selectedGroup ? (
          <>
            <h2>{selectedGroup}</h2>
            <div className="notes-display">
              {notes.map((note) => (
                <div key={note.id} className="note">
                  <p>{note.content}</p>
                  <small>Created: {note.createdAt}</small>
                  <small>Updated: {note.updatedAt}</small>
                </div>
              ))}
            </div>
            <div className="note-input">
              <input
                type="text"
                placeholder="Write a note..."
                value={newNote}
                onChange={(e) => setNewNote(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && handleNoteSave()}
              />
              <button
                className={newNote ? 'active' : ''}
                onClick={handleNoteSave}
              >
                ➤
              </button>
            </div>
          </>
        ) : (
          <div className="placeholder">
            <img src="/welcome.png" alt="Welcome" />
            <h1>Pocket Notes</h1>
            <p>Send and receive messages without keeping your phone online.</p>
            <p>Use Pocket Notes on up to 4 linked devices and 1 mobile phone</p>
            <p style={{ fontSize: '12px', marginTop: '10px' }}>
              🔒 end-to-end encrypted
            </p>
          </div>
        )}
      </div>

      {/* Create Group Popup */}
      {showPopup && (
        <div className="popup-overlay" onClick={() => setShowPopup(false)}>
          <div className="popup" onClick={(e) => e.stopPropagation()}>
            <h3>Create New Group</h3>
            <input
              type="text"
              value={newGroupName}
              onChange={(e) => setNewGroupName(e.target.value)}
              placeholder="Group Name"
            />
            <label style={{ marginBottom: '10px', display: 'block' }}>
              Choose Color:
              <input
                type="color"
                value={newGroupColor}
                onChange={(e) => setNewGroupColor(e.target.value)}
                style={{ marginLeft: '10px' }}
              />
            </label>
            <button onClick={handleAddGroup}>Create</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
